import API_URL from './apiUrl';


const SIGN_IN = `
mutation SignIn($username: String!, $password: String!) {
  signIn(username: $username, password: $password)
}
`

const SIGN_UP = `
mutation SignUp($username: String!, $password: String!) {
  signUp(username: $username, password: $password)
}`

const TODO_LISTS_QUERY = `
  query TodoLists($where: TodoListWhere) {
    todoLists(where: $where) {
      id
      title
    }
}
`;

export async function fetchTodoLists() {
  try {
    const response = await fetch(API_URL, {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({
        query: TODO_LISTS_QUERY,
        variables: { 
          where: {
            owner:{
              id:token,
              username: username
            }
        } },
      }),
    });

    const result = await response.json();
    console.log('API Response:', result);  // Ajoute ce log pour voir la réponse

    if (result.errors) {
      throw new Error(result.errors.map(error => error.message).join(', '));
    }

    return result.data.todoLists;
  } catch (error) {
    console.error('Error fetching todo lists:', error);
    throw new Error('Could not fetch todo lists');
  }
}

export function signIn(username, password) {
    return fetch(API_URL, {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({
        query: SIGN_IN,
        variables: {
          username: username,
          password: password,
        },
      }),
    })
      .then((response) => response.json())
      .then((jsonResponse) => {
        if (jsonResponse.errors) {
          throw jsonResponse.errors[0];
        }
        return jsonResponse.data.signIn; // Retourne le token (ou autre réponse) après connexion
      })
      .catch((error) => {
        throw error;
      });
  }

  export function signUp(username, password) {
    return fetch(API_URL, {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({
        query: SIGN_UP,
        variables: {
          username: username,
          password: password,
        },
      }),
    })
      .then((response) => response.json())
      .then((jsonResponse) => {
        if (jsonResponse.errors) {
          throw jsonResponse.errors[0];
        }
        return jsonResponse.data.signUp; // Retourne le token après inscription
      })
      .catch((error) => {
        throw error;
      });
  }
