import React from 'react'
import { TokenContext, UsernameContext } from '../Context/Context'
import {useState, useContext} from 'react'
import {View, Text, TextInput, Button} from 'react-native'

export default function SignInScreen ({ navigation }) {
    const [usernameInput, setUsernameInput] = useState('');
    const [passwordInput, setPasswordInput] = useState('');
    const [error, setError] = useState(null);
    const [token, setToken] = useContext(TokenContext);
    const [username, setUsername] = useContext(UsernameContext);

    const handleSignIn = async () => {
      console.log(username)
      try {

        const tokenResponse = await signIn(usernameInput, passwordInput);
        setToken(tokenResponse); 
        setUsername(usernameInput); 

        navigation.navigate('HomeScreen'); 

      } catch (e) {
        setError('Sign-in failed. Please check your credentials.');
      }
    };
    const handleSignUp = async () => {
      try {
        const tokenResponse = await signUp(usernameInput, passwordInput);
        setToken(tokenResponse);  // Mise à jour du token après inscription
        setUsername(usernameInput);  // Mise à jour du nom d'utilisateur
        navigation.navigate('HomeScreen');  // Naviguer vers l'écran principal après inscription réussie
      } catch (e) {
        setError('Sign-up failed. Please try again.');
      }
    };
    return (
      <View>
      <Text>Sign In</Text>
        {error && <Text>{error}</Text>}
        <TextInput
          placeholder="Username"
          value={usernameInput}
          onChangeText={setUsernameInput}
        />
        <TextInput
          placeholder="Password"
          value={passwordInput}
          secureTextEntry
          onChangeText={setPasswordInput}
        />
        <Button title="Sign In" onPress={() => {handleSignIn()}} />

        <Text>Don't have an account? Sign up below!</Text>
        <Button title="Sign Up" onPress={() =>handleSignUp()} />
    </View>
      );
  }
