import React, { useState } from 'react'
import {
  StyleSheet,
  View,
  TextInput,
  Button,
  Text,
  FlatList,
  Switch
} from 'react-native'

import todoData from '../Helpers/todoData'
import TodoItem from '../Screen/TodoItemScreen'

export default function TodoList () {
  const [todos, setTodos] = useState(todoData)
  const [count, setCount] = useState(todos.filter(item => item.done).length)
  const [newTodoText, setNewTodoText] = useState('')
  const [filter, setFilter] = useState('all');

  const updateCount = offset => {
    setCount(count + offset)
  }

  const updateItem = id => {
    const newTodos = todos.map((item) => {return {id: item.id, content: item.content, done: (item.id == id) ? ! item.done : item.done}})
    setTodos(newTodos)
    setCount(newTodos.filter(item => item.done).length)
  }

  const deleteTodo = id => {
    const newTodos = todos.filter(item => item.id != id)
    setTodos(newTodos)
    setCount(newTodos.filter(item => item.done).length)
  }

  const addNewTodo = () => {
    if (newTodoText == '') return
    setTodos([
      ...todos,
      {
        id: todos.length ? Math.max(...todos.map(item => item.id)) + 1 : 1,
        content: newTodoText,
        done: false
      }
    ])
    setNewTodoText('')
  }

  const checkAll = () => {
    const newTodos = todos.map((item) => {return {id: item.id, content: item.content, done: true}})
    setTodos(newTodos)
    setCount(newTodos.filter(item => item.done).length);
    console.log(newTodos)
  }

  const reversedcheckAll = () => {
    const newTodos = todos.map((item) => {return {id: item.id, content: item.content, done: false}})
    setTodos(newTodos)
    setCount(newTodos.filter(item => item.done).length);
    console.log(newTodos)
  }

  const getFilteredTodos = () => {
    if (filter === 'done') {
      return todos.filter(item => item.done);
    } else if (filter === 'not_done') {
      return todos.filter(item => !item.done);
    } else {
      return todos;
    }
  };

  return (
    <TouchableOpacity onPress={() => props.navigation.navigate('Details', {id: props.item.id})}>
      <Item
        id={props.item.id}
        title={props.item.title}
        delete={() => props.delete(props.item.id)}
      />
    </TouchableOpacity>
  )
}

const styles = StyleSheet.create({
  textInput_group: {
    flexDirection: 'row'
  },
  textinput_view: {
    margin: 12,
    borderWidth: 1,
    padding: 10
  },
  buttoninput_view: {
    margin: 12,
    paddingTop: 3
  }
})
