import React, { useState, useEffect } from 'react';
import {
  View,
  Text,
  FlatList,
  Button,
  TextInput,
  StyleSheet,
} from 'react-native';
import { useQuery, gql } from '@apollo/client';

const TODO_LISTS_QUERY = gql`
  query TodoLists($where: TodoListWhere) {
    todoLists(where: $where) {
      id
      title
    }
  }
`;
export default function TodoListScreen() {
  const { loading, error, data, refetch } = useQuery(TODO_LISTS_QUERY, {
    variables: { where: {} },
  });
  const [newTodoTitle, setNewTodoTitle] = useState('');

  const handleAddTodo = () => {
    // Logique pour ajouter une nouvelle Todo
  };

  const handleDeleteTodo = (id) => {
    // Logique pour supprimer une Todo
  };

  if (loading) return <Text>Loading...</Text>;
  if (error) return <Text>Error: {error.message}</Text>;

  return (
    <View style={styles.container}>
      <Text>TodoList</Text>
      <FlatList
        data={data.todoLists}
        keyExtractor={(item) => item.id.toString()}
        renderItem={({ item }) => (
          <View style={styles.todoItem}>
            <Text>{item.title}</Text>
            <Button
              title="Delete"
              onPress={() => handleDeleteTodo(item.id)}
            />
          </View>
        )}
      />
      <TextInput
        style={styles.input}
        placeholder="New Todo Title"
        value={newTodoTitle}
        onChangeText={setNewTodoTitle}
      />
      <Button title="Add Todo" onPress={handleAddTodo} />
    </View>
  );
}
